function handleChangeTrack(event) {
    sessionStorage.setItem("TRACK_NAME", event.innerText)
    var matchingId = (element) => element.id == event.name
    sessionStorage.setItem("TRACK_ID", vt_musicas.findIndex(matchingId))
    console.log(sessionStorage.getItem("TRACK_ID"));

    window.location = "./song.html";
}